import{d as a}from"./app.974cb5f8.js";const n={},e=a('<h2 id="介绍" tabindex="-1"><a class="header-anchor" href="#介绍" aria-hidden="true">#</a> 介绍</h2><p><code>npx</code> 是 <code>npm v5.2.0</code> 版本引入的工具，其可以快速体验想要尝试的包而不必执行 <code>npm init</code>、<code>npm install</code>、<code>配置 scripts</code> 等步骤。</p><p>默认情况下，它会去检查包是否存在（<code>node_modules/.bin</code> 及环境变量 <code>$PATH</code>），如果存在则执行，不存在则临时安装再运行。</p><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token comment"># 查看指令的帮助信息</span>\nnpx -h\n</code></pre></div><h3 id="_1-作用" tabindex="-1"><a class="header-anchor" href="#_1-作用" aria-hidden="true">#</a> 1. 作用</h3><ol><li>避免与全局包冲突，可以测试新的版本</li><li>可直接测试 github 上的模块</li></ol><h3 id="_2-参数" tabindex="-1"><a class="header-anchor" href="#_2-参数" aria-hidden="true">#</a> 2. 参数</h3><ol><li><code>--no-install</code>：强制使用本地模块，不存在则报错</li><li><code>--ignore-existing</code>：强制使用远程模块</li></ol><h2 id="执行包" tabindex="-1"><a class="header-anchor" href="#执行包" aria-hidden="true">#</a> 执行包</h2><h3 id="_1-global" tabindex="-1"><a class="header-anchor" href="#_1-global" aria-hidden="true">#</a> 1. global</h3><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token function">npm</span> i mocha -g\nmocha\n</code></pre></div><h3 id="_2-node-modules" tabindex="-1"><a class="header-anchor" href="#_2-node-modules" aria-hidden="true">#</a> 2. node_modules</h3><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token function">npm</span> i mocha -D\n./node_modules/.bin/mocha\n</code></pre></div><h3 id="_3-package" tabindex="-1"><a class="header-anchor" href="#_3-package" aria-hidden="true">#</a> 3. package</h3><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token function">npm</span> i mocha -D\n\n<span class="token comment"># 配置 package.json</span>\n<span class="token punctuation">{</span>\n  scripts: <span class="token punctuation">{</span>\n  \t<span class="token string">&quot;test&quot;</span><span class="token builtin class-name">:</span> <span class="token string">&quot;mocha&quot;</span>\n  <span class="token punctuation">}</span>\n<span class="token punctuation">}</span>\n\n<span class="token comment"># 执行 scripts</span>\n<span class="token function">npm</span> run <span class="token builtin class-name">test</span>\n</code></pre></div><h3 id="_4-npx" tabindex="-1"><a class="header-anchor" href="#_4-npx" aria-hidden="true">#</a> 4. npx</h3><div class="language-bash ext-sh"><pre class="language-bash"><code>npx mocha\n</code></pre></div>',17);n.render=function(a,n){return e};export default n;
